
const http = require('http');
const fs = require('fs');
//As server holds the instantiation of a createServer function of http request
const server = http.createServer((req, res) => {
    console.log(req.url, req.method);

    //set header-content
    res.setHeader('Content-header', 'text/html');
    // res.write('<h1>CodeNaion</h1>');
    // res.write('<p>Hello Its me PIyush GArg coding Enthusiast.</p>');
    // res.end();
    //for sending HTML response file

    //Routing in page
    let path = './views/';
    switch(req.url){
        case '/':
            path += 'index.html';
            res.statusCode = 200;    //200 means OK
            break;
        case '/signin':
            path += 'signin.html';
            res.statusCode = 200;     
            break;
        case '/sign-in':
            res.statusCode = 301;    //301 means resource move permanenly
            res.setHeader('Location', '/signin');
            res.end();    
            break;
        case '/signup':
            path += 'Signup.html';
            res.statusCode = 200;
            break;
        default:
            path += '404.html';
            res.statusCode = 404;  //404 means Not Found
            break;
    }
    fs.readFile(path, (err, data) => {
        if(err){
            res.end();
        }else{
            res.write(data);
            res.end();
        }
    });
});

//For actual request we use listener
server.listen(3000, 'localhost', () => {
    console.log('listening for request on port 3000');
});